import React from "react";
import { Button } from "./components/ui/button";
import { Card, CardContent } from "./components/ui/card";
import { Badge } from "./components/ui/badge";

export default function App() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 text-white p-10">
      <Badge className="mb-4 bg-blue-500/10 text-blue-400 border-blue-500/20 px-4 py-2">
        Welcome to AI News
      </Badge>
      <h1 className="text-4xl font-bold mb-6">Stay Ahead of the AI Revolution</h1>
      <p className="text-lg mb-6 text-slate-300">
        Get personalized AI news, breakthrough research, and industry insights.
      </p>
      <div className="flex gap-4">
        <Button>Get Started</Button>
        <Button variant="outline">Watch Demo</Button>
      </div>

      <div className="mt-10 grid md:grid-cols-3 gap-4">
        {[1, 2, 3].map((n) => (
          <Card key={n}>
            <CardContent>
              <h3 className="text-xl font-bold mb-2 text-white">Feature {n}</h3>
              <p className="text-slate-300">This is a description of feature {n}.</p>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
